INSERT INTO courses (title, description, createdAt, updatedAt) VALUES ('Борьба против коррупции', 'Курс состоит из модулей, которые охватывают теоретические и практические вопросы противодействия коррупции в бизнесе (включая квазигосударственный сектор в Казахстане). Теоретическая часть курса охватывает: теории возникновения и существования коррупции, виды и формы, основные теории о том, почему люди совершают коррупционные преступления, новые формы коррупции;' '2024-12-09 18:49:31.137+05', '2024-12-09 18:49:31.137+05');

